import React from 'react';
import { NumberInput } from 'your-number-input-library'; // Replace with your actual import
import { AddIcon, RemoveIcon } from 'your-icon-library'; // Replace with your actual icons import

function CustomNumberInput(props, ref) {
    const inc = props.onIncrement;
    const dec = props.onDecrement;

    return (
        <NumberInput
            slots={{
                root: StyledInputRoot,
                input: StyledInput,
                incrementButton: StyledButton,
                decrementButton: StyledButton,
            }}
            slotProps={{
                incrementButton: {
                    children: <AddIcon />,
                    className: 'increment',
                    onClick: inc, // Call the onIncrement function
                },
                decrementButton: {
                    children: <RemoveIcon />,
                    onClick: dec, // Call the onDecrement function
                },
            }}
            {...props}
            ref={ref}
        />
    );
}

// Use React.forwardRef with the CustomNumberInput component
const ForwardedCustomNumberInput = React.forwardRef(CustomNumberInput);

export default ForwardedCustomNumberInput;

export function QuantityInput(props) {
    return (
        <ForwardedCustomNumberInput
            aria-label="Quantity Input"
            min={1}
            onChange={props.onChange}
            value={props.value}
            onIncrement={props.onIncrement}
            onDecrement={props.onDecrement}
        />
    );
}

const blue = {
    100: '#daecff',
    200: '#b6daff',
    300: '#66b2ff',
    400: '#3399ff',
    500: '#007fff',
    600: '#0072e5',
    800: '#004c99',
};

const grey = {
    50: '#f6f8fa',
    100: '#eaeef2',
    200: '#d0d7de',
    300: '#afb8c1',
    400: '#8c959f',
    500: '#6e7781',
    600: '#57606a',
    700: '#424a53',
    800: '#32383f',
    900: '#24292f',
};

const StyledInputRoot = styled('div')(
    ({ theme }) => `
                font-family: IBM Plex Sans, sans-serif;
                font-weight: 400;
                color: ${theme.palette.mode === 'dark' ? grey[300] : grey[500]};
                display: flex;
                flex-flow: row nowrap;
                justify-content: left;
                align-items: left;
                `,
);

const StyledInput = styled('input')(
    ({ theme }) => `
                font-size: 0.875rem;
                font-family: inherit;
                font-weight: 400;
                line-height: 1.375;
                color: ${theme.palette.mode === 'dark' ? grey[300] : grey[900]};
                background: ${theme.palette.mode === 'dark' ? grey[900] : '#fff'};
                border: 1px solid ${theme.palette.mode === 'dark' ? grey[700] : grey[200]};
                border-radius: 4px;
                margin: 0 4px;
                padding: 10px 12px;
                outline: 0;
                min-width: 0;
                width: 4rem;
                text-align: left;

                &:hover {
                    border - color: ${blue[400]};
    }

                &:focus {
                    border - color: ${blue[400]};
                box-shadow: 0 0 0 3px ${theme.palette.mode === 'dark' ? blue[500] : blue[200]};
    }

                &:focus-visible {
                    outline: 0;
    }
                `,
);

const StyledButton = styled('button')(
    ({ theme }) => `
                font-family: IBM Plex Sans, sans-serif;
                font-size: 0.875rem;
                box-sizing: border-box;
                line-height: 1.5;
                border: 0;
                border-radius: 999px;
                color: ${theme.palette.mode === 'dark' ? blue[300] : blue[600]};
                background: transparent;
                width: 40px;
                height: 40px;
                display: flex;
                flex-flow: row nowrap;
                justify-content: left;
                align-items: left;
                transition-property: all;
                transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
                transition-duration: 120ms;

                &:hover {
                    background: ${theme.palette.mode === 'dark' ? blue[800] : blue[100]};
                cursor: pointer;
    }

                &:focus-visible {
                    outline: 0;
    }

                &.increment {
                    order: 1;
    }
                `,
);
