import React, { useState } from 'react';
import './Auth.css'; // Import your CSS file if needed

function Auth() {
  const [isSignup, setIsSignup] = useState(false);

  const handleSignupClick = () => {
    setIsSignup(true);
  };

  const handleLoginClick = () => {
    setIsSignup(false);
  };

  return (
    <div className="container">
      <div className={`slider ${isSignup ? 'moveslider' : ''}`}></div>
      <div className="btn">
        <button className={`login ${!isSignup ? 'active' : ''}`} onClick={handleLoginClick}>
          Login
        </button>
        <button className={`signup ${isSignup ? 'active' : ''}`} onClick={handleSignupClick}>
          Signup
        </button>
      </div>

      <div className="form-section">
        {isSignup ? (
          <div className="signup-box">
            <input type="text" className="name ele" placeholder="Enter your name" />
            <input type="email" className="email ele" placeholder="youremail@email.com" />
            <input type="password" className="password ele" placeholder="password" />
            <input type="password" className="password ele" placeholder="Confirm password" />
            <button className="clkbtn">Signup</button>
          </div>
        ) : (
          <div className="login-box">
            <input type="email" className="email ele" placeholder="youremail@email.com" />
            <input type="password" className="password ele" placeholder="password" />
            <button className="clkbtn">Login</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default Auth;
