import { createSlice } from "@reduxjs/toolkit"


export const userImageSlice = createSlice({
    name: "userImage",
    initialState: { value: JSON.parse(localStorage.getItem('user')).image || '' },
    reducers: {
        setUserImage: (state, action) => {
            state.value = action
            //localStorage.setItem("products", JSON.stringify(state.value));
        },
    }
})
export const { setUserImage } = userImageSlice.actions
export default userImageSlice.reducer
