import React from 'react'

import { Unstable_NumberInput as NumberInput } from '@mui/base/Unstable_NumberInput';
import { styled } from '@mui/system';
import RemoveIcon from '@mui/icons-material/Remove';
import AddIcon from '@mui/icons-material/Add';



export function QuantityInput(props) {
    const handleIncrement = () => {
        // Check if props and props.value exist before accessing it
        if (props && props.value !== undefined) {
            console.log(props);
            // Calculate the new value by incrementing the current value
            const newValue = parseInt(props.value, 10) + 1;
            // Call the onChange handler with the new value
            props.onChange(newValue);
        }
    };

    const handleDecrement = () => {
        if (props && props.value !== undefined) {

            // Calculate the new value by decrementing the current value
            const newValue = parseInt(props.value, 10) - 1;
            // Ensure the new value is not less than the minimum value (min={1})
            const finalValue = Math.max(newValue, 1);
            // Call the onChange handler with the new value
            props.onChange(finalValue);
        }
    };

    const CustomNumberInput = React.forwardRef(function CustomNumberInput(props, ref,) {
        return (
            <NumberInput
                slots={{
                    root: StyledInputRoot,
                    input: StyledInput,
                    incrementButton: StyledButton,
                    decrementButton: StyledButton,
                }}
                slotProps={{
                    incrementButton: {
                        children: <AddIcon />,
                        className: 'increment',
                        onClick: handleIncrement()
                    },
                    decrementButton: {
                        children: <RemoveIcon />,
                        onClick: handleDecrement()

                    },
                }}
                {...props}
                ref={ref}
            />
        );
    });

    return <CustomNumberInput aria-label="Quantity Input" min={1} onChange={props.onChange} value={props.value} />;
}

const blue = {
    100: '#daecff',
    200: '#b6daff',
    300: '#66b2ff',
    400: '#3399ff',
    500: '#007fff',
    600: '#0072e5',
    800: '#004c99',
};

const grey = {
    50: '#f6f8fa',
    100: '#eaeef2',
    200: '#d0d7de',
    300: '#afb8c1',
    400: '#8c959f',
    500: '#6e7781',
    600: '#57606a',
    700: '#424a53',
    800: '#32383f',
    900: '#24292f',
};

const StyledInputRoot = styled('div')(
    ({ theme }) => `
                font-family: IBM Plex Sans, sans-serif;
                font-weight: 400;
                color: ${theme.palette.mode === 'dark' ? grey[300] : grey[500]};
                display: flex;
                flex-flow: row nowrap;
                justify-content: left;
                align-items: left;
                `,
);

const StyledInput = styled('input')(
    ({ theme }) => `
                font-size: 0.875rem;
                font-family: inherit;
                font-weight: 400;
                line-height: 1.375;
                color: ${theme.palette.mode === 'dark' ? grey[300] : grey[900]};
                background: ${theme.palette.mode === 'dark' ? grey[900] : '#fff'};
                border: 1px solid ${theme.palette.mode === 'dark' ? grey[700] : grey[200]};
                border-radius: 4px;
                margin: 0 4px;
                padding: 10px 12px;
                outline: 0;
                min-width: 0;
                width: 4rem;
                text-align: left;

                &:hover {
                    border - color: ${blue[400]};
    }

                &:focus {
                    border - color: ${blue[400]};
                box-shadow: 0 0 0 3px ${theme.palette.mode === 'dark' ? blue[500] : blue[200]};
    }

                &:focus-visible {
                    outline: 0;
    }
                `,
);

const StyledButton = styled('button')(
    ({ theme }) => `
                font-family: IBM Plex Sans, sans-serif;
                font-size: 0.875rem;
                box-sizing: border-box;
                line-height: 1.5;
                border: 0;
                border-radius: 999px;
                color: ${theme.palette.mode === 'dark' ? blue[300] : blue[600]};
                background: transparent;
                width: 40px;
                height: 40px;
                display: flex;
                flex-flow: row nowrap;
                justify-content: left;
                align-items: left;
                transition-property: all;
                transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
                transition-duration: 120ms;

                &:hover {
                    background: ${theme.palette.mode === 'dark' ? blue[800] : blue[100]};
                cursor: pointer;
    }

                &:focus-visible {
                    outline: 0;
    }

                &.increment {
                    order: 1;
    }
                `,
);
