import React, { useEffect, useState } from 'react';
import './PaymentStatus.css';
import { useLocation } from 'react-router-dom'; // Import useLocation
import axios from 'axios';

function PaymentStatus() {
    const location = useLocation(); // Use useLocation hook to access the current location
    const searchParams = new URLSearchParams(location.search); // Get query parameters from location
    const paymentId = searchParams.get("payment_id"); // Get the payment_id query parameter

    const [paymentStatus, setPaymentStatus] = useState(null);

    useEffect(() => {
        const handleCheckPaymentStatus = async () => {
            try {
                const resp = await axios.post(`http://localhost:8000/api/payment/verify/${paymentId}`);
                console.log(resp);
                setPaymentStatus(resp.data.result.status);
            } catch (err) {
                console.log(err);
            }
        }
        if (paymentId) {
            handleCheckPaymentStatus();
        }
    }, [paymentId]);

    console.log(paymentStatus);

    return (
        paymentStatus != null &&
        (paymentStatus === "SUCCESS" ?
            <div id="orderContainer">
                <div id="check"><i className="fas fa-check-circle"></i></div>
                <div id="aboutCheck">
                    <h2 style={{ color: "black" }}>Order Placed Successfully!</h2>
                    <p>We've sent you an email with the Order details.</p>
                </div>
            </div> :
            <div id="orderContainer">
                <i style={{ color: "red" }} className="fas fa-exclamation"></i>
                <div id="aboutCheck">
                    <h2 style={{ color: "black" }}>Payment failure!</h2>
                </div>
            </div>)
    );
}

export default PaymentStatus;
