import React, { useContext, useEffect, useState } from 'react'
import { AuthContext } from '../../context/AuthReducer';

import MySlider from '../Slider/MySlider';
import Content from '../Content/Content';
import Footer from '../Footer/Footer';
import Header from '../Header/Header';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import jwtInterceptor from '../../helpers/jwtInterceptor';

function Home() {
    const [state, dispatch] = useContext(AuthContext)
    let showOneToast = false
    useEffect(() => {
        console.log(showOneToast);
        console.log(state.user)
        if (!showOneToast && state.user) {
            showOneToast = true
            toast.success(state.user.msg, {
                position: toast.POSITION.TOP_RIGHT
            });
        }
    }, [])
    const inter = () => {
        const user = JSON.parse(localStorage.getItem("user"))
        const resp = jwtInterceptor.get("http://localhost:8000/api/auth/test/" + user.data._id, {
            withCredentials: true,
        })
        console.log(resp.data)

    }
    return (
        <div>     <button onClick={() => inter()} >clic</button>
            <ToastContainer />

            <Header /><MySlider />
            <Content />
            <Footer /></div>
    )
}

export default Home
