import React, { Component } from 'react';
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import { Form, Button, Alert } from 'react-bootstrap';

class EditProfile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            email: '',
            address: '',
            city: 'Ariana',
            phoneNumber: '',
            password: '',
            confirmPassword: '',
            stateReturned: null,
            msgReturned: null,
        };
    }

    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };

    handleSubmit = (event) => {
        event.preventDefault();
        // Add your form submission logic here
        // Update this.state.stateReturned and this.state.msgReturned based on the result
    };

    render() {
        return (
            <div>
                <Header />
                <div className="container bootstrap snippets bootdey" style={{ marginTop: "10px" }}>
                    <h1 className="text-primary">Editer le profil</h1>
                    <hr />
                    <div className="row">
                        <div className="col-md-7 personal-info">
                            <h3>Informations personnelles</h3>
                            <Form onSubmit={this.handleSubmit}>
                                <Form.Group className="mb-3" controlId="Username">
                                    <Form.Label style={{ fontSize: "12px" }}>Username</Form.Label>
                                    <Form.Control
                                        style={{ fontSize: "12px" }}
                                        type="text"
                                        name="username"
                                        value={this.state.username}
                                        onChange={this.handleChange}
                                        placeholder="Username"
                                    />
                                </Form.Group>
                                {/* Other form fields go here */}
                                <Button
                                    style={{ fontSize: "12px", marginBottom: "50px" }}
                                    variant="primary"
                                    type="submit"
                                >
                                    Sauvegarder
                                </Button>
                            </Form>
                            {this.state.stateReturned != null ? (
                                <Alert variant="success" style={{ width: "40rem" }}>
                                    <Alert.Heading style={{ fontSize: 15 }}>
                                        {this.state.msgReturned}
                                    </Alert.Heading>
                                </Alert>
                            ) : this.state.stateReturned === false ? (
                                <Alert variant="danger" style={{ width: "42rem" }}>
                                    <Alert.Heading style={{ fontSize: 15 }}>
                                        {this.state.msgReturned}
                                    </Alert.Heading>
                                </Alert>
                            ) : null}
                        </div>
                    </div>
                </div>
                <Footer />
            </div>
        );
    }
}

export default EditProfile;
