import React from 'react'
import './Header.css';
import { useLocation, useNavigate } from 'react-router-dom';

function Header() {
    const navigate = useNavigate()
    const location = useLocation()
    const handleGetProfile = (e) => {
        e.preventDefault()
        const user = JSON.parse(localStorage.getItem("user"))
        if (!user) navigate("/auth")
    }
    const manageHeader = (id) => {
        if (location.pathname != "/") {
            navigate("/", { state: id })
        } else {
            const element = document.getElementById(id);
            element.scrollIntoView({ behavior: 'smooth' });
        }
    }
    return (
        <header>
            <section>
                <div id="container">
                    <div id="shopName"><a onClick={() => navigate("/")}> <b>SHOP</b>LANE </a></div>
                    <div id="collection">
                        <div id="clothing"><a style={{ cursor: 'pointer' }} onClick={() => manageHeader("CLOTHING")}> CLOTHING </a></div>
                        <div id="accessories"><a style={{ cursor: 'pointer' }} onClick={() => manageHeader("ACCESSORIES")}> ACCESSORIES </a></div>
                    </div>
                    <div id="search">
                        <i className="fas fa-search search"></i>
                        <input type="text" id="input" name="searchBox" placeholder="Search for Clothing and Accessories" />
                    </div>
                    <div id="user">
                        <a href="cart.html"> <i className="fas fa-shopping-cart addedToCart"><div id="badge"> 0 </div></i></a>
                        <i onClick={(e) => handleGetProfile(e)} className="fas fa-user-circle userIcon"></i>
                    </div>
                </div>
            </section>
        </header>
    )
}

export default Header
