import React, { useState } from 'react';
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import { useEffect } from 'react';

function EditProfile() {
    useEffect(() => {
        const data = JSON.parse(localStorage.getItem('user')).data
        setState({
            ...state,
            "username": data.username,
            "email": data.email,
        })
    }, [])

    const [state, setState] = useState({
        address: '',
        number: '',
        password: '',
        confirmPassword: '',
        cin: "",
        response: {
            stateReturned: null,
            msgReturned: null,
        }
    });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setState({
            ...state,
            [name]: value
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
    };
    console.log(state);

    return (
        <div>
            <Header />
            <br />
            <br />
            <br />
            <br />

            <div class="container-xl px-4 mt-4">
                <div class="row">
                    <div class="col-xl-4">
                        <div class="card mb-4 mb-xl-0">
                            <div class="card-header">Profile Picture</div>
                            <div class="card-body text-center">
                                <img class="img-account-profile rounded-circle mb-2" src="http://bootdey.com/img/Content/avatar/avatar1.png" height="100px" alt="" />
                                <div class="small font-italic text-muted mb-4">JPG or PNG no larger than 5 MB</div>
                                <button class="btn btn-primary" type="button">Upload new image</button>
                            </div>
                        </div>
                    </div>
                    <div class="col-xl-8">
                        <div class="card mb-4">
                            <div class="card-header">Account Details</div>
                            <div class="card-body">
                                <form>
                                    <div class="mb-3">
                                        <label class="small mb-1" for="inputUsername">Username (how your name will appear to other users on the site)</label>
                                        <input class="form-control" id="inputUsername" type="text" placeholder="Enter your username" value="username" />
                                    </div>
                                    <div class="mb-3">
                                        <label class="small mb-1" for="inputEmailAddress">Email address</label>
                                        <input class="form-control" id="inputEmailAddress" type="email" placeholder="Enter your email address" value={state.email} disabled />
                                    </div>
                                    <div class="row gx-3 mb-3">
                                        <div class="col-md-6">
                                            <label class="small mb-1" for="inputPhone">Phone number</label>
                                            <input class="form-control" id="inputPhone" type="tel" placeholder="Enter your phone number" value={state.number} />
                                        </div>
                                        <div class="col-md-6">
                                            <label class="small mb-1" for="inputCin">CIN</label>
                                            <input class="form-control" id="inputCin" type="text" placeholder="Enter your CIN" value={state.cin} />
                                        </div>
                                    </div>
                                    <div class="row gx-3 mb-3">
                                        <div class="col-md-6">
                                            <label class="small mb-1" for="inputPassword">Password</label>
                                            <input class="form-control" id="inputPassword" type="text" placeholder="Enter your new password" value={state.password} />
                                        </div>
                                        <div class="col-md-6">
                                            <label class="small mb-1" for="inputConfirmPassword">Confirm Password</label>
                                            <input class="form-control" id="inputConfirmPassword" type="text" placeholder="Confirm your new password" value={state.confirmPassword} />
                                        </div>
                                    </div>
                                    <button class="btn btn-primary" type="button">Save changes</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <Footer />
        </div>
    );
}

export default EditProfile;
