import React, { useEffect, useState } from 'react'
import "./Cart.css"
import { useSelector } from 'react-redux'
function Cart() {
    const cartItems = useSelector((state) => state.products.value)
    const [totalAmount, setTotalAmount] = useState(() => {
        let s = 0
        for (let i = 0; i < cartItems.length; i++) {
            s += cartItems[i].price
        }
        return s
    })
    const placeOrder = () => {

    }
    return (
        <div>
            <button onClick={() => placeOrder()}>Place Order</button>
            <div id="cartContainer">
                {cartItems.map((cartItem, index) => (
                    <div key={index} id="boxContainer">
                        <div id="box">
                            <img src={cartItem.image[0]} alt={cartItem.productName} />
                            <h3>{cartItem.productName}</h3>
                            <h4>Amount: {cartItem.price} DNT</h4>
                        </div>
                    </div>
                ))}
                <div id="totalContainer">
                    <div id="total">
                        <h2>Total Amount</h2>
                        <h4>Amount: {totalAmount} DNT</h4>
                        <div id="button">
                            <button>
                                <a href="/orderPlaced.html">Place Order</a>
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Cart
