import React, { useEffect, useState } from 'react'
const product = {
    img: "https://assets.myntassets.com/h_1440,q_100,w_1080/v1/assets/images/7579188/2018/11/5/08a7b230-ee8f-46c0-a945-4e835a3c01c01541402833619-United-Colors-of-Benetton-Men-Sweatshirts-1271541402833444-1.jpg",
    name: "maryoul",
    brand: "Puma",
    price: "200"
}
import './Content.css';
import axios from 'axios';


function Content() {
    const [products, setProducts] = useState([])

    const handleGetProducts = async () => {
        try {
            const products = await axios.get("http://localhost:8000/api/product/getallproducts")
            setProducts(product)
        } catch (err) {
            console.log(err);
        }
    }
    useEffect(async () => {
        await handleGetProducts()
    }, [])

    const DynamicClothingSection = (props) => {
        return (
            <div id="box">
                <a href="#">
                    <img src={"http://localhost:8000/images/" + product.imgages[0]} alt={product.productName} />
                    <div id="details">
                        <h3>{product.productName}</h3>
                        <h4>{product.brand}</h4>
                        <h2>{product.price} DNT</h2>
                    </div>
                </a>
            </div>)
    }


    return (
        <div>
            <div id="mainContainer">
                <h1 id="forPointing1"> clothing for men and women </h1>
                <div id="containerClothing">
                    {
                        (new Array(products.length).fill(0)).map((val, index) => (
                            products[i].type == "clothing" && <DynamicClothingSection key={index} />
                        ))
                    }
                </div>
                <h1 id="forPointing2"> accessories for men and women </h1>

                <div id="containerAccessories">
                    {
                        (new Array(products.length).fill(0)).map((val, index) => (
                            products[i].type == "accessories" && <DynamicClothingSection key={index} />
                        ))
                    }
                </div>

            </div>
        </div>
    );
}

export default Content
