import React from 'react'
const product = {
    img: "https://assets.myntassets.com/h_1440,q_100,w_1080/v1/assets/images/7579188/2018/11/5/08a7b230-ee8f-46c0-a945-4e835a3c01c01541402833619-United-Colors-of-Benetton-Men-Sweatshirts-1271541402833444-1.jpg",
    name: "maryoul",
    brand: "Puma",
    price: "200"
}

function Content() {
    const DynamicClothingSection = () => {
        return (
            <div id="box">
                <a href="#">
                    <img src={product.img} alt={product.name} />
                    <div id="details">
                        <h3>{product.name}</h3>
                        <h4>{product.brand}</h4>
                        <h2>{product.price} DNT</h2>
                    </div>
                </a>
            </div>)
    }


    return (
        <div>
            {/* Display the counter value */}
            <div id="badge">{counter}</div>

            {/* Render clothing and accessories sections */}
            <div id="containerClothing">
                {contentTitle.map((item) =>
                    item.isAccessory ? null : (
                        <DynamicClothingSection key={item.id} ob={item} />
                    )
                )}
            </div>
            <div id="containerAccessories">
                {contentTitle.map((item) =>
                    item.isAccessory ? (
                        <DynamicClothingSection key={item.id} ob={item} />
                    ) : null
                )}
            </div>
        </div>
    );
}

export default Content
