import React, { useEffect } from 'react'
import './Header.css';
import { useLocation, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';

function Header() {
    const navigate = useNavigate()
    const location = useLocation()

    const state = useSelector((state) => state.products.value)

    const handleGetProfile = (e) => {
        e.preventDefault()
        const user = JSON.parse(localStorage.getItem("user"))
        if (!user)
            navigate("/auth")
        else
            navigate("/profile")

    }

    const manageHeader = (id) => {
        if (location.pathname != "/") {
            navigate("/", { state: id })
        } else {
            const element = document.getElementById(id);
            element.scrollIntoView({ behavior: 'smooth' });
        }
    }
    return (
        <header>
            <section>
                <div id="container">
                    <div id="shopName"><a style={{ cursor: 'pointer' }} onClick={() => navigate("/")}> <b>SHOP</b>LANE </a></div>
                    <div id="collection">
                        <div id="clothing"><a style={{ cursor: 'pointer' }} onClick={() => manageHeader("CLOTHING")}> CLOTHING </a></div>
                        <div id="accessories"><a style={{ cursor: 'pointer' }} onClick={() => manageHeader("ACCESSORIES")}> ACCESSORIES </a></div>
                    </div>
                    <div id="search">
                        <i className="fas fa-search search"></i>
                        <input type="text" id="input" name="searchBox" placeholder="Search for Clothing and Accessories" />
                    </div>
                    <div id="user">
                        <a style={{ color:"grey", cursor: 'pointer' }} onClick={() => navigate("/cart")}> <i className="fas fa-shopping-cart addedToCart"><div id="badge"> {state.length} </div></i></a>
                        <img onClick={(e) => handleGetProfile(e)} src='http://localhost:8000/images/default-user.png' height='45px'/>
                    </div>
                </div>
            </section>
        </header>
    )
}

export default Header
