import React, { useState } from 'react';
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import { Form, Button, Alert } from 'react-bootstrap';
import { useEffect } from 'react';

function EditProfile() {
    useEffect(() => {
        const data = JSON.parse(localStorage.getItem('user')).data
        setState({
            ...state,
            "username": data.username,
            "email": data.email,
        })
    }, [])
    const [state, setState] = useState({
        address: '',
        number: '',
        password: '',
        confirmPassword: '',
        response: {
            stateReturned: null,
            msgReturned: null,
        }
    });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setState({
            ...state,
            name: value
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
    };
    console.log(state);

    return (
        <div>
            <Header />
            <div className="container bootstrap snippets bootdey" style={{ marginTop: "10px" }}>
                <h1 className="text-primary">Editer le profil</h1>
                <hr />
                <div className="row">
                    <div className="col-md-7 personal-info">
                        <h3>Informations personnelles</h3>
                        <Form onSubmit={handleSubmit}>
                            <Form.Group className="mb-3" controlId="Username">
                                <Form.Label style={{ fontSize: "12px" }}>Username</Form.Label>
                                <Form.Control
                                    style={{ fontSize: "12px" }}
                                    type="text"
                                    name="username"
                                    value={state.username}
                                    onChange={handleChange}
                                    placeholder="Username"
                                />
                            </Form.Group>
                            {/* Other form fields go here */}
                            <Button
                                style={{ fontSize: "12px", marginBottom: "50px" }}
                                variant="primary"
                                type="submit"
                            >
                                Sauvegarder
                            </Button>
                        </Form>
                        {state.response.stateReturned != null ? (
                            <Alert variant="success" style={{ width: "40rem" }}>
                                <Alert.Heading style={{ fontSize: 15 }}>
                                    {state.response.msgReturned}
                                </Alert.Heading>
                            </Alert>
                        ) : state.response.stateReturned === false ? (
                            <Alert variant="danger" style={{ width: "42rem" }}>
                                <Alert.Heading style={{ fontSize: 15 }}>
                                    {state.response.msgReturned}
                                </Alert.Heading>
                            </Alert>
                        ) : null}
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default EditProfile;
