import React, { useEffect, useState } from 'react'

import axios from 'axios';
import './ProductDetails.css';
import Footer from '../Footer/Footer';
import Header from '../Header/Header';


function ProductDetails() {
    const [productDetails, setProductDetails] = useState()
    const [picSelected, setPicSelected] = useState(null)

    const handleGetProducts = async () => {
        try {
            const products = await axios.get("http://localhost:8000/api/product/getallproducts")
            setProductDetails(products.data.data[1])
        } catch (err) {
            console.log(err);
        }
    }

    useEffect(() => {
        handleGetProducts()
    }, [])

    const handleImageClick = (pic) => {
        setPicSelected("http://localhost:8000/images/" + pic)
    }
    return (
        productDetails && (
            <div>
                <Header />
                <div className="containerProduct">
                    <div className="containerD">
                        <div className="imageSection">
                            <img id="imgDetails" src={picSelected || "http://localhost:8000/images/" + productDetails.images[0]} alt={productDetails.name} />
                        </div>
                        <div className="productDetails">
                            <h1>{productDetails.name}</h1>
                            <h4>{productDetails.brand}</h4>
                            <div className="details">
                                <h3>Rs {productDetails.price}</h3>
                                <h3>Description</h3>
                                <p>{productDetails.description}</p>
                            </div>
                            <div className="productPreview">
                                <h3>Product Preview</h3>
                                {productDetails.images.map((photo, index) => (
                                    <img
                                        key={index}
                                        id="previewImg"
                                        src={"http://localhost:8000/images/" + photo}
                                        alt={`Preview ${index}`}
                                        onClick={() => handleImageClick(photo)}
                                    />
                                ))}
                            </div>
                            <div className="button">
                                <button>Add to Cart</button>
                            </div>
                        </div>
                    </div>
                </div>
                <Footer />
            </div>)
    )
}

export default ProductDetails
