import React, { useEffect, useState } from 'react'
import './Content.css';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';


function Content() {
    const [products, setProducts] = useState([])
    const navigate = useNavigate()
    const location = useLocation();

    const handleGetProducts = async () => {
        try {
            const products = await axios.get("http://localhost:8000/api/product/getallproducts")
            setProducts(products.data.data)
        } catch (err) {
            console.log(err);
        }
    }
    useEffect(() => {
        handleGetProducts()
        if (location.state) {
            console.log(location.state);
            location.state.scrollIntoView({ behavior: 'smooth' })
        }
    }, [])

    const goToProductDetails = (product) => {
        navigate('/product', { state: product });
    }

    const DynamicClothingSection = (props) => {
        return (
            <div id="box">
                <a onClick={() => goToProductDetails(props.product)}>
                    <img src={"http://localhost:8000/images/" + props.product.images[0]} alt={props.product.productName} />
                    <div id="details">
                        <h3>{props.product.productName}</h3>
                        <h4>{props.product.brand}</h4>
                        <h2>{props.product.price} DNT</h2>
                    </div>
                </a>
            </div>)
    }

    return (
        <div>
            <div id="mainContainer">
                <h1 id="CLOTHING"> clothing for men and women </h1>
                <div id="containerClothing">
                    {
                        (new Array(products.length).fill(0)).map((val, index) => (
                            products[index].type == "clothing" && <DynamicClothingSection product={products[index]} key={index} />
                        ))
                    }
                </div>
                <h1 id="ACCESSORIES"> accessories for men and women </h1>

                <div id="containerAccessories">
                    {
                        (new Array(products.length).fill(0)).map((val, index) => (
                            products[index].type == "accessories" && <DynamicClothingSection product={products[index]} key={index} />
                        ))
                    }
                </div>

            </div>
        </div>
    );
}

export default Content
