import React, { useEffect, useState } from 'react'
import './ProductDetails.css';

import Footer from '../Footer/Footer';
import Header from '../Header/Header';
import { useLocation } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { addItem, removeItem } from '../../redux/features/products';

import { Unstable_NumberInput as NumberInput } from '@mui/base/Unstable_NumberInput';
import { styled } from '@mui/system';
import RemoveIcon from '@mui/icons-material/Remove';
import AddIcon from '@mui/icons-material/Add';



function ProductDetails() {
    const [productDetails, setProductDetails] = useState()
    const [picSelected, setPicSelected] = useState(null)

    // LOCATION USED TO GET THE VALUE SENT FROM USE NAVIGATE
    const location = useLocation();

    const dispatch = useDispatch()

    // SET THE STATE OF CART PRODUCT HAS BEEN SET TO CART OR NO
    const [cartState, setCartState] = useState(false)

    let state = useSelector((state) => state.products.value)


    useEffect(() => {
        setProductDetails(location.state)

        // CHECK IF THE PRODUCT HAS BEEN ADDED IN CART ALREADY OR NO
        let getStateLength = state.length
        state = state.filter(item => item._id !== location.state._id)
        if (state.length < getStateLength) setCartState(true)
    }, [])

    const handleImageClick = (pic) => {
        setPicSelected("http://localhost:8000/images/" + pic)
    }

    const handleAddItemToCart = () => {
        dispatch(addItem(location.state))
        setCartState(true)
    }
    const handleRemoveItemFromCart = () => {
        dispatch(removeItem(location.state))
        setCartState(false)
    }

    return (
        productDetails && (
            <div>
                <Header />
                <div className="containerProduct">
                    <div className="containerD">
                        <div className="imageSection">
                            <img id="imgDetails" src={picSelected || "http://localhost:8000/images/" + productDetails.images[0]} alt={productDetails.productName} />
                        </div>
                        <div className="productDetails" style={{ display: "flex", flexDirection: "column" }}>
                            <h3 style={{ color: "black" }}>{productDetails.productName}</h3>

                            <div className="top" style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                                <button
                                    className="button"
                                    onClick={cartState ? () => handleRemoveItemFromCart() : () => handleAddItemToCart()}
                                >
                                    {cartState ? "Remove from cart" : "Add to cart"}
                                </button>
                            </div>
                            <h3 style={{ color: '#4682B4' }}>{productDetails.brand}</h3>

                            <div className="details">
                                <h3>{productDetails.price} DNT</h3>
                                <h4>Description</h4>
                                <p>{productDetails.description}</p>
                            </div>
                            <QuantityInput />
                            <div className="productPreview">
                                <h4>Product Preview</h4>
                                {productDetails.images.map((photo, index) => (
                                    <img
                                        key={index}
                                        id="previewImg"
                                        src={"http://localhost:8000/images/" + photo}
                                        alt={`Preview ${index}`}
                                        onClick={() => handleImageClick(photo)}
                                    />
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
                <Footer />
            </div>)
    )
}

const CustomNumberInput = React.forwardRef(function CustomNumberInput(props, ref) {
    return (
        <NumberInput
            slots={{
                root: StyledInputRoot,
                input: StyledInput,
                incrementButton: StyledButton,
                decrementButton: StyledButton,
            }}
            slotProps={{
                incrementButton: {
                    children: <AddIcon />,
                    className: 'increment',
                },
                decrementButton: {
                    children: <RemoveIcon />,
                },
            }}
            {...props}
            ref={ref}
        />
    );
});

export function QuantityInput() {
    return <CustomNumberInput aria-label="Quantity Input" min={1} max={99} />;
}

const blue = {
    100: '#daecff',
    200: '#b6daff',
    300: '#66b2ff',
    400: '#3399ff',
    500: '#007fff',
    600: '#0072e5',
    800: '#004c99',
};

const grey = {
    50: '#f6f8fa',
    100: '#eaeef2',
    200: '#d0d7de',
    300: '#afb8c1',
    400: '#8c959f',
    500: '#6e7781',
    600: '#57606a',
    700: '#424a53',
    800: '#32383f',
    900: '#24292f',
};

const StyledInputRoot = styled('div')(
    ({ theme }) => `
                font-family: IBM Plex Sans, sans-serif;
                font-weight: 400;
                color: ${theme.palette.mode === 'dark' ? grey[300] : grey[500]};
                display: flex;
                flex-flow: row nowrap;
                justify-content: left;
                align-items: left;
                `,
);

const StyledInput = styled('input')(
    ({ theme }) => `
                font-size: 0.875rem;
                font-family: inherit;
                font-weight: 400;
                line-height: 1.375;
                color: ${theme.palette.mode === 'dark' ? grey[300] : grey[900]};
                background: ${theme.palette.mode === 'dark' ? grey[900] : '#fff'};
                border: 1px solid ${theme.palette.mode === 'dark' ? grey[700] : grey[200]};
                border-radius: 4px;
                margin: 0 4px;
                padding: 10px 12px;
                outline: 0;
                min-width: 0;
                width: 4rem;
                text-align: left;

                &:hover {
                    border - color: ${blue[400]};
    }

                &:focus {
                    border - color: ${blue[400]};
                box-shadow: 0 0 0 3px ${theme.palette.mode === 'dark' ? blue[500] : blue[200]};
    }

                &:focus-visible {
                    outline: 0;
    }
                `,
);

const StyledButton = styled('button')(
    ({ theme }) => `
                font-family: IBM Plex Sans, sans-serif;
                font-size: 0.875rem;
                box-sizing: border-box;
                line-height: 1.5;
                border: 0;
                border-radius: 999px;
                color: ${theme.palette.mode === 'dark' ? blue[300] : blue[600]};
                background: transparent;
                width: 40px;
                height: 40px;
                display: flex;
                flex-flow: row nowrap;
                justify-content: left;
                align-items: left;
                transition-property: all;
                transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
                transition-duration: 120ms;

                &:hover {
                    background: ${theme.palette.mode === 'dark' ? blue[800] : blue[100]};
                cursor: pointer;
    }

                &:focus-visible {
                    outline: 0;
    }

                &.increment {
                    order: 1;
    }
                `,
);
export default ProductDetails
