import { createSlice } from "@reduxjs/toolkit"

export const productsSlice = createSlice({
    name: "products",
    initialState: { value: JSON.parse(localStorage.getItem('products')) || [] },
    reducers: {
        addItem: (state, action) => {
            if (!state.value.includes(action.payload)) {
                state.value.push(action.payload)
                localStorage.setItem("products", JSON.stringify(state.value));
            }
        },
        removeItem: (state, action) => {
            state.value.filter(item => item !== action.payload);
            localStorage.setItem("products", JSON.stringify(state.value));
        },
    }
})
export const { addItem, removeItem } = productsSlice.actions
export default productsSlice.reducer
