import { createContext, useEffect, useReducer, useRef } from "react";

export const AuthContext = createContext();

const INITIAL_STATE = {
    user: AuthContextProvider.state.user || JSON.parse(localStorage.getItem("user")) || null,
    loading: false,
    error: null,
};

const AuthReducer = (state, action) => {
    switch (action.type) {
        case "LOGIN_START":
            return {
                user: null,
                loading: true,
                error: null,
            };
        case "LOGIN_SUCCESS":
            return {
                user: action.payload,
                loading: false,
                error: null,
            };
        case "LOGIN_FAILURE":
            return {
                user: null,
                loading: false,
                error: action.payload,
            };
        case "LOGOUT":
            return {
                user: null,
                loading: false,
                error: null,
            };
        case "LAST_SAVED_DATA": return action.payload
        default:
            return state;
    }
};

export const AuthContextProvider = () => {
    const initialStateRef = useRef(INITIAL_STATE)
    const [state, dispatch] = useReducer(AuthReducer, initialStateRef);

    useEffect(() => {
        //console.log(state.user);
        //localStorage.setItem("user", JSON.stringify(state.user));
        initialStateRef.current = state;
    }, [state.user]);

    return [state, dispatch]
    /*return (
        <AuthContext.Provider
            value={{
                user: state.user,
                loading: state.loading,
                error: state.error,
                dispatch,
            }}
        >
            <>
        </AuthContext.Provider>
    );*/
};
