import React, { useEffect } from 'react'
import './Header.css';
import { Link, useNavigate } from 'react-router-dom';

function Header() {
    const navigate = useNavigate()
    const handleGetProfile = ()=>{
        const user = JSON.parse(localStorage.getItem("user"))
        if(!user) navigate("/auth")
    }
    return (
        <header>
            <section>
                <div id="container">
                    <div id="shopName"><a href="/"> <b>SHOP</b>LANE </a></div>
                    <div id="collection">
                        <div id="clothing"><a href="#forPointing1"> CLOTHING </a></div>
                        <div id="accessories"><a href="#forPointing2"> ACCESSORIES </a></div>
                    </div>
                    <div id="search">
                        <i className="fas fa-search search"></i>
                        <input type="text" id="input" name="searchBox" placeholder="Search for Clothing and Accessories" />
                    </div>
                    <div id="user">
                        <a href="cart.html"> <i className="fas fa-shopping-cart addedToCart"><div id="badge"> 0 </div></i></a>
                        <a href="/" onClick={() => handleGetProfile()}> <i className="fas fa-user-circle userIcon"></i> </a>
                    </div>
                </div>
            </section>
        </header>
    )
}

export default Header
