import React, { useEffect, useState } from 'react'
import './PaymentStatus.css';
import { useSearchParams } from 'react-router-dom';
import axios from 'axios';

function PaymentStatus() {
    const [serachParams] = useSearchParams()
    const [PaymentStatus, setPaymentStatus] = useState(null)
    useEffect(() => {
        const handleCheckPaymentStatus = async () => {
            try {
                const resp = await axios.post("http://localhost:8000/api/payment/verify/" + serachParams.get("payment_id"))
                setPaymentStatus(resp.data.result.status)
            } catch (err) {
                console.log(err);
            }
        }
        handleCheckPaymentStatus()
    }, [])
    console.log(PaymentStatus);
    return (
        PaymentStatus != null &&
        (PaymentStatus === "SUCCESS" ?
            <div id="orderContainer">
                <div id="check"><i class="fas fa-check-circle"></i></div>
                <div id="aboutCheck">
                    <h2 style={{ color: "black" }}> Order Placed Successfully! </h2>
                    <p> We've sent you an email with the Order details. </p>
                </div>
            </div> :
            <div id="orderContainer">
                <i style={{ color: "red" }} class="fas fa-exclamation"></i>
                <div id="aboutCheck">
                    <h2 style={{ color: "black" }}> Payment failure! </h2>
                </div>
            </div>)
    )
}

export default PaymentStatus
