import React, { useEffect } from 'react'
import './ProductDetails.css';


function ProductDetails() {
    const [productDetails, setProductDetails] = useState([])
    

    const handleGetProducts = async () => {
        try {
            const products = await axios.get("http://localhost:8000/api/product/getallproducts")
            setProductDetails(products[0].data.data)
        } catch (err) {
            console.log(err);
        }
    }

    useEffect(() => {
        handleGetProducts()
    }, [])
    return (
        <div className="containerProduct">
            {productDetails && (
                <div className="containerD">
                    <div className="imageSection">
                        <img id="imgDetails" src={selectedImage || productDetails.preview} alt={productDetails.name} />
                    </div>
                    <div className="productDetails">
                        <h1>{productDetails.name}</h1>
                        <h4>{productDetails.brand}</h4>
                        <div className="details">
                            <h3>Rs {productDetails.price}</h3>
                            <h3>Description</h3>
                            <p>{productDetails.description}</p>
                        </div>
                        <div className="productPreview">
                            <h3>Product Preview</h3>
                            {productDetails.photos.map((photo, index) => (
                                <img
                                    key={index}
                                    id="previewImg"
                                    src={photo}
                                    alt={`Preview ${index}`}
                                    onClick={() => this.handleImageClick(photo)}
                                />
                            ))}
                        </div>
                        <div className="button">
                            <button onClick={this.handleAddToCart}>Add to Cart</button>
                            <span id="badge">{counter}</span>
                        </div>
                    </div>
                </div>
            )}
        </div>
    )
}

export default ProductDetails
