import React, { useEffect, useState } from 'react'
import './ProductDetails.css';

import Footer from '../Footer/Footer';
import Header from '../Header/Header';
import { useLocation } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { addItem } from '../../redux/features/products';


function ProductDetails() {
    const [productDetails, setProductDetails] = useState()
    const [picSelected, setPicSelected] = useState(null)
    const location = useLocation();
    const dispatch = useDispatch()

    const [cartState, setCartState] = useState(false)
    const state = useSelector((state) => state.products.value)


    useEffect(() => {
        console.log(state);
        setProductDetails(location.state)
        if (state.includes(productDetails)) {
            setCartState(true)
        }
    }, [])

    const handleImageClick = (pic) => {
        setPicSelected("http://localhost:8000/images/" + pic)
    }

    const handleAddItemToCart = () => {
        dispatch(addItem(location.state))
        setCartState(true)
    }

    return (
        productDetails && (
            <div>
                <Header />
                <div className="containerProduct">
                    <div className="containerD">
                        <div className="imageSection">
                            <img id="imgDetails" src={picSelected || "http://localhost:8000/images/" + productDetails.images[0]} alt={productDetails.productName} />
                        </div>
                        <div className="productDetails">
                            <h1>{productDetails.productName}</h1>
                            <h4>{productDetails.brand}</h4>
                            <div className="details">
                                <h3>{productDetails.price} DNT</h3>
                                <h3>Description</h3>
                                <p>{productDetails.description}</p>
                            </div>
                            <div className="productPreview">
                                <h3>Product Preview</h3>
                                {productDetails.images.map((photo, index) => (
                                    <img
                                        key={index}
                                        id="previewImg"
                                        src={"http://localhost:8000/images/" + photo}
                                        alt={`Preview ${index}`}
                                        onClick={() => handleImageClick(photo)}
                                    />
                                ))}
                            </div>
                            <div className="button">
                                <button onClick={() => handleAddItemToCart()}>{cartState ? "Remove from cart" : "Add to cart"}</button>
                            </div>
                        </div>
                    </div>
                </div>
                <Footer />
            </div>)
    )
}

export default ProductDetails
